const arr = [1, 2, 3, 4, 5, 6, 7],
  main = document.querySelector(".main");
console.log(arr);
const radioButtonsBlock = document.querySelector(".view__rbs"),
  radioButtons = document.querySelectorAll(".view__rb"),
  headingEl = document.querySelector(".view-heading"),
  paragraphEl = document.querySelector(".view-paragraph"),
  instructionLinkEl = document.querySelector(".view-inctruction-link"),
  videoSrcEl = document.querySelector(".view-videoSrc"),
  videoEl = document.querySelector(".view__video");
fetch("../json/instructions.json")
  .then((e) => {
    if (!e.ok) throw new Error("Failed to load JSON");
    return e.json();
  })
  .then((e) => {
    radioButtonsBlock.addEventListener("click", (t) => {
      if (t.target && "radio" === t.target.type) {
        const r = t.target.id;
        try {
          const {
            heading: t,
            paragraph: o,
            instructionLink: n,
            videoSrc: i,
          } = e[r];
          (headingEl.textContent = t),
            (paragraphEl.textContent = o),
            (instructionLinkEl.href = n),
            (videoSrcEl.src = i),
            videoEl.load();
        } catch (e) {
          console.error(e);
        }
      }
    });
  });
